
var h = require('h')
var o = require('../')

var cb, style

document.body.appendChild(
  h('div#content',
   h('h1',
   INPUT = cb = h('input', {type: 'checkbox'}),
    {style: {
      'background-color': 
        style = o.transform(
          o.input(cb, 'checked', 'change'), 
          function (t) {
            return t ? 'red' : 'blue'
          }
        )
    }},
    style)
  )
)
