var h = require('h')
var o = require('../')

document.body.appendChild(
  h('div', 
    fn = h('input', {placeholder: 'first name'}), 
    ln = h('input', {placeholder: 'last name'}), 
    h('h1', 'fullname: ', 
      o.compute([o.input(fn), o.input(ln)], function (f, l) {
        return l + ', ' + f
      })
    )
  )
)


